        axios.get(BASEURL+`/profile/my_image_list/${patientId}`)
        .then(function (response) {
            //setImageList(response.data);
            const imageList = response.data;
            const imagePromises = imageList.map((image) =>
            axios.get(`${BASEURL}${image.image}`, { responseType: 'blob' })
            );
            console.log(response);

            Promise.all(imagePromises)
            .then((responses) => {
            const files = responses.map((response, index) => {
            const blob = response.data;
            const filename = `image_${index}.jpg`;
            return new File([blob], filename, { type: 'image/jpeg' });
          });

          console.log(files);
          // Use the files as needed
          const decryptionPromises  = files.map((file) =>{
            decrypt(file, response.data.key)
          });






        })
        .catch((error) => {
          console.error(error);
        });
            
          })
        .catch(function (error) {
            console.log(error);
        });
    
    }, [])




  return (
    <div>
        <ListGroup className="list-unstyled">
            {imageList.map((image) => (
                <li key={image.id}>
                    <ListGroup.Item>
                        <Figure.Image
                        src={`http://127.0.0.1:8000${image.image}`}
                        />
                </ListGroup.Item>
                </li>
            ))}
        </ListGroup>
        <UploadImageButton 
        imageList = {imageList}
        setImageList = {setImageList}/>
    </div>
  )
}

export default PatientImagesPage
