                const ctx = canvas.getContext('2d');
                ctx.drawImage(img, 0, 0);
                const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
                const decryptedImageData1 = decryptImageData1(imageData, key);
                const decryptedImageDataFinal = scramble(decryptedImageData1, (source, diff) => {
                    return rightRotate(source, diff);
                }, false, key)
                imageData.data.set(decryptedImageDataFinal);
                ctx.putImageData(imageData, 0, 0);
                canvas.toBlob((blob) => {
                    const decryptedFile = new File([blob], file.name, { type: file.type });
                    resolve(decryptedFile);
                  }, file.type);
                };
                img.src = reader.result;
                };
                reader.readAsDataURL(file);
    });
}








