  return (
    <div>
        <ListGroup className="list-unstyled">
            {imageList.map((myImage) => (
                <li key={myImage.id}>
                    <ListGroup.Item>
                        <Figure.Image
                        src={`http://127.0.0.1:8000${myImage.image}`}
                        />
                </ListGroup.Item>
                </li>
            ))}
        </ListGroup>
        <UploadImageButton 
        imageList = {imageList}
        setImageList = {setImageList}/>
    </div>
  )
}

