import { useState, useContext } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { BASEURL } from './baseURL';
import { AppContext } from './App';


function LoginForm(){

  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const { loggedIn, setLoggedIn } = useContext(AppContext);
  const { isDoctor, setIsDoctor } = useContext(AppContext);

  function LoginUser(){
    axios.post(BASEURL + "/auth/jwt/create/",{
      username: username,
      password: password
    }).then(function (response) {
      console.log(response)
      localStorage.setItem("access",JSON.stringify(response.data["access"]));
      localStorage.setItem("refresh",JSON.stringify(response.data["refresh"]));
      setLoggedIn(true);
        axios.get(BASEURL + "/auth/users/me/",{
          headers: {
            authorization: "JWT "+ JSON.parse(localStorage.getItem("access"))
          }
        }).then(function (response) {
          setIsDoctor(response.data["isDoctor"]);
          console.log(response)
        }).catch(function (error) {
          console.log(error);
        });
    })
    .catch(function (error) {
      console.log(error);
    });
  }

  return(
    <Form style={
      {margin: 10}
    }>

    <Form.Group className="mb-3" controlId="LoginForm.username">
      <Form.Label>Username</Form.Label>
      <Form.Control
          type= "text"
          placeholder="Username"
          required
          onChange={(e) => setUsername(e.target.value)}
        />
    </Form.Group>

    <Form.Group className="mb-3" controlId="LoginForm.password">
      <Form.Label>Password</Form.Label>
      <Form.Control
          type= "password"
          placeholder="Password"
          required
          onChange={(e) => setPassword(e.target.value)}
      />
    </Form.Group>

    <Button 
      variant="primary" 
      className="mb-3" 
      type="submit"
      onClick={(e) => {e.preventDefault(); LoginUser()}}>
        Login
    </Button>

    </Form>
  );
}

export default LoginForm;
