import React from 'react'
import { useState, useEffect, useContext } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Figure from 'react-bootstrap/Figure';
import axios from 'axios';
import { AppContext } from './App';
import { BASEURL } from './baseURL';
import UploadImageButton from './UploadImageButton'
import {decrypt} from './encrypt.js'



function PatientImagesPage() {

    const {patientId} = useContext(AppContext);
    const [imageList, setImageList] = useState([]);
    const [decryptedImagesList, setDecryptedImages] = useState([]);



    useEffect(() =>{
        axios
        .get(BASEURL+`/profile/my_image_list/${patientId}`)
        .then(function (response) {
            const imageList = response.data;
            const decryptionPromises = imageList.map((image) =>{
                const url = BASEURL + image.image;
                return axios
                .get(url, { responseType: 'blob' })
                .then((response) => {
                    const blob = response.data;
                    // const originalFileExtension = image.image.split('.').pop();
                    // const filename = `image_${image.id}.${originalFileExtension}`;
                    const filename = image.image.split('/').pop(); 
                    const file = new File([blob], filename, { type: image.mime_type });
                    return decrypt(file, image.key);
                });
            });
            Promise.all(decryptionPromises)
            .then((decryptedFiles) => {
            const decryptedImages = decryptedFiles.map((file) =>
                URL.createObjectURL(file)
            );
            setDecryptedImages(decryptedImages);
            console.log(decryptedImages)
            })
            .catch((error) => {
            console.error(error);
            });
        })
        .catch((error) => {
        console.error(error);
        });
    }, []);




  return (
    <div>
        <ListGroup className="list-unstyled">
            {decryptedImagesList.map((imageUrl, index) => (
                <li key={index}>
                    <ListGroup.Item>
                        <Figure.Image
                        src={imageUrl}
                        />
                </ListGroup.Item>
                </li>
            ))}
        </ListGroup>
        <UploadImageButton 
        imageList = {imageList}
        setImageList = {setImageList}/>
    </div>
  )
}

export default PatientImagesPage
