import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import axios from 'axios';
import { BASEURL } from './baseURL';


function DoctorSlotAdd() {

  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [slotList, setSlotList] = useState([]);


  useEffect(()=>{
    axios.get(BASEURL + `/profile/book_slots_list/${localStorage.getItem("doctor_id")}`,{
    }).then(function (response) {
      console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
    });
    slotList.map((slots) => (console.log(slots.id)));
  }, [slotList]);

  function addEntry()
  {
    const end = new Date(date).getTime() + 30*60*1000;
    const myNewSlot = {
      doctorProfile: JSON.parse(localStorage.getItem("doctor_id")),
      booking_date: date,
      start_time: time,
      end_time: new Date(end).toTimeString().slice(0, 8)
    }
    setSlotList([...slotList, myNewSlot]);
    console.log(myNewSlot);
    axios.post(BASEURL + "/profile/book_slots_list/",{
      headers: {
          authorization: "JWT "+ JSON.parse(localStorage.getItem("access"))
        },
        doctorProfile: myNewSlot.doctorProfile,
        booking_date: myNewSlot.booking_date,
        start_time: myNewSlot.start_time,
        end_time: myNewSlot.end_time
      }).then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
    });
  }

  return (
    <OverlayTrigger 
      trigger = "click"
      key = "right"
      placement="right"
      overlay={
        <Form style={
          {margin: 10 , backgroundColor: "grey"}
        }>
          <Form.Group className="mb-3" controlId="book_date" >
              <Form.Label>Date</Form.Label>
              <Form.Control
                  type = "date"
                  placeholder="Booking date"
                  required
                  onChange={(e) => setDate(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="book_time">
              <Form.Label>Time</Form.Label>
              <Form.Control
                  type = "time"
                  placeholder="Booking time"
                  required
                  onChange={(e) => setTime(e.target.value)}
              />
              <Button 
                variant="primary" 
                className="mb-3" 
                type="submit"
                onClick={(e) => {
                  e.preventDefault(); 
                  addEntry();
                  }}>
                  Confirm
              </Button>
            </Form.Group>
          </Form>
      }
      >
      <Button variant="secondary">Add Slots</Button>
      </OverlayTrigger>
  )
}

export default DoctorSlotAdd
